package main

import "fmt"

func main() {
	fmt.Println(maxNumberOfBalloons("nlaebolkoo"))
	fmt.Println(maxNumberOfBalloons("loonbalxballpoon"))
	fmt.Println(maxNumberOfBalloons(""))
}

func maxNumberOfBalloons(text string) int {
	var ar = make([]int, 5)

	for _, r := range text {
		switch r {
		case 'a':
			ar[0]++
		case 'b':
			ar[1]++
		case 'o':
			ar[2]++
		case 'l':
			ar[3]++
		case 'n':
			ar[4]++
		}
	}
	ar[2] /= 2
	ar[3] /= 2

	var min int = 10000
	for i := 0; i < 5; i++ {
		if ar[i] < min {
			min = ar[i]
		}
	}
	return min
}
